const readline = require ("readline-sync");
const tarea = [];

exports.agregartarea = () => {
    const indicador = readline.question("indicador de la tarea");
    const descripcion = readline.question("descripcion de la tarrea");

    const tarea = {
        indicador,
        descripcion,
        completada: false,
    };

    exports.eliminartarea=()=>{
        const indicador = readline.question("indicador de tarea eliminada");
        const tareaIndex = tarea.findIndex((tarea) => tarea.indicador == indicador );
        if (tareaIndex !== -1) {
            tarea.splice(tareaIndex, 1);
            console.log("tarea eliminada");
        }

    };

    exports.tareacompletada = () =>{
        const indicador = readline.question("indicador de la tarea a compretar");
        const tarea = tarea.find((tarea) => tarea.indicador == indicador);
        if (tarea) {
            tarea.completada = true;
            console.log("tarea completada");
        }
        else { console.log("no se encontraron tareas pendientes")}
    };

    exports.mostrartarea = () => {
        console.log("task list");
        tarea.forEach((tarea) => {
            const status = tarea.completada ? "completada" : "pendiente";
            console.log(`${tarea.indicador} ${tarea.descripcion} ${status}`);
            
        });
    }
}