const showTasks = require("readline");
const main = showTasks.createInterface({
    input:process.stdin,
    output:process.stdout,
});

const modulo = require ("TaskList");

async function main () {
    try{
        
        let exit = false;
        while (!exit) {
            console.log("--- Gestor de Tareas ---");
            console.log("1. Agregar Tarea");
            console.log("2. Eliminar Tarea");
            console.log("3. Marcar Tarea como Completada");
            console.log("4. Mostrar Tareas");
            console.log("5. Salir");
            const option = readline.question ("escoger una opcion: ");

            switch (option){
                case "1":
                    const caso1 = await modulo.agregartarea();
                    console.log (caso1);
                    break;
                case "2":
                    const caso2 = await modulo.eliminartarea ();
                    console.log (caso2);
                    break;
                case "3":
                    const caso3 = await modulo.tareacompletada ();
                    console.log (caso3);
                    break;
                case "4":
                    const caso4=await modulo.mostrartarea ();
                    console.log (caso4);
                    break;
                case "5":
                    exit = true;
                console.log("Hasta Pronto");
                break;
                default:
                    console.log ("opcion incorrecta, intente de nuevo");
            }

            console.log("\n");
    }    
    } catch (error) {
        console.log(error);
    }
}

main();