const showTasks = require("readline");
const main = showTasks.createInterface({
    input:process.stdin,
    output:process.stdout,
});

const modulo = require ("./TaskList");

function main () {
    let exit = false;
    while (!exit) {
        console.log("--- Gestor de Tareas ---");
        console.log("1. Agregar Tarea");
        console.log("2. Eliminar Tarea");
        console.log("3. Marcar Tarea como Completada");
        console.log("4. Mostrar Tareas");
        console.log("5. Salir");
        const option = readline.question ("escoger una opcion: ");

        switch (option){
            case "1":
                modulo.agregartarea();
                break;
            case "2":
                modulo.eliminartarea ();
                break;
            case "3":
                modulo.tareacompletada ();
                break;
            case "4":
                modulo.mostrartarea ();
                break;
            case "5":
                exit = true;
            console.log("Hasta Pronto");
            break;
            default:
                console.log ("opcion incorrecta, intente de nuevo");
        }

        console.log("\n");
    }
}

main();